mapper.insert:
SELECT FYP_BOARD_SEQ.NEXTVAL FROM DUAL

INSERT INTO FYP_BOARD(    BNO 
				       , TITLE 
				       , CONTENT 
				       , WRITER  )
              VALUES(    #{bno}
             		   , #{title}
             		   , #{content}
             		   , #{writer}  )

mapper.select:
SELECT STORED_FILE_NAME, ORG_FILE_NAME
FROM FYP_FILE
WHERE FILE_NO = #{FILE_NO}

mapper.update:
UPDATE FYP_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

UPDATE FYP_BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}

UPDATE FYP_BOARD SET
HEART = HEART+1
WHERE BNO = #{bno}
